<script setup lang="ts">
import { onMounted, reactive, ref } from 'vue'
//路由
import { useRouter } from 'vue-router'
const router = useRouter()

interface Lists {
  id: number
  name: string
  icon: string
  path: string
}
let list: Array<Lists> = [
  {
    id: 1,
    name: '首页',
    icon: 'iconfont icon-home',
    path: '/'
  },
  {
    id: 2,
    name: '任务',
    icon: 'iconfont icon-task',
    path: '/task'
  },
  {
    id: 3,
    name: '统计',
    icon: 'iconfont icon-count',
    path: '/count'
  },
  {
    id: 4,
    name: '设置',
    icon: 'iconfont icon-set',
    path: '/set'
  }
]
let initLi: any = ref(null)
let active: any = ref(null)
let topIndex: number = 0 //初始化top值
//切换tab
function tabToggle(path: string, index: number) {
  topIndex = 50 * index //根据当前点击的li下标 减去点击前的li的下标
  active.value.style.top = topIndex + 'px'
  let arr: any = [...initLi.value] //获取所有li的dom
  arr.forEach((item: any) => {
    item.classList.remove('color-font') //清除初始化字体颜色
  })
  arr[index].classList.add('color-font') //当前点击的元素改变颜色
  // console.log(arr[index])
  //路由跳转
  router.push(path)
}
//login部分
let userInfo: any = reactive({
  username: localStorage.userName || '未登录'
})
//点击头像跳转login页面
function login() {
  router.push('/login')
}

onMounted(() => {
  window.addEventListener('setItemEvent', () => {
    userInfo.username = localStorage.userName
  })
  //默认选择第一个li
  initLi.value[0].classList.add('color-font')
})
</script>

